import React from 'react';
import styles from './ListItem.less';
import {
  DeleteOutlined,
  ReloadOutlined,
  LoadingOutlined,
  EyeOutlined,
  StopOutlined,
  ShareAltOutlined,
} from '@ant-design/icons';
import { Space, Divider, Button, Image, Progress } from 'antd';
import { ListItem, ModelStatus, getStatus } from './index';

interface Props {
  item: ListItem;
  style: {};
  height?: number;
  width?: number;
  retry: (item: ListItem) => void;
  cancelTransfer: (item: ListItem) => void;
  deleteItem: (item: ListItem) => void;
  showDetail: (item: ListItem) => void;
}

function ListItemComp(props: Props) {
  const { item, retry, cancelTransfer, deleteItem, showDetail, style } = props;

  const share = (item: ListItem) => {
    // share fn
  };
  const modelOptions = () => {
    const text = item.status;
    if (text === ModelStatus.FAIL || text === ModelStatus.CANCEL) {
      return (
        <>
          <ReloadOutlined title={'重试'} onClick={() => retry(item)} />
          <DeleteOutlined title={'删除'} onClick={() => deleteItem(item)} />
        </>
      );
    } else if (
      text === ModelStatus.PENDING ||
      text === ModelStatus.WAITING ||
      text === ModelStatus.WAITING_MERGING
    ) {
      return (
        <>
          <StopOutlined title={'取消'} onClick={() => cancelTransfer(item)} />
          <DeleteOutlined title={'删除'} onClick={() => deleteItem(item)} />
        </>
      );
    } else {
      return (
        <>
          <EyeOutlined title={'查看'} onClick={() => showDetail(item)} />
          <ShareAltOutlined title={'分享'} onClick={() => share(item)} />
          <DeleteOutlined title={'删除'} onClick={() => deleteItem(item)} />
        </>
      );
    }
  };
  const modelStatusComponent = () => {
    const text = item.status;
    switch (text) {
      case ModelStatus.FAIL:
        return <Progress type="circle" percent={100} status="exception" />;
      case ModelStatus.CANCEL:
        return <StopOutlined style={{ fontSize: 100 }} />;
      case ModelStatus.PENDING:
        return <Progress type="circle" percent={item.statusInfo?.progress} />;
      case ModelStatus.SUCCESS:
        return (
          <Image
            width={128}
            height={128}
            src={item.previewImage}
            fallback={
              'data:image/png;base64,'
            }
          />
        );
      case ModelStatus.WAITING:
        return <> 等待队列 {item.statusInfo?.queue} </>;
      case ModelStatus.WAITING_MERGING:
        return <> 转换预处理 </>;
    }
  };
  const defaultItemWidth = props.width ? props.width : 168;
  const defaultItemHeight = props.height ? props.height : 200;
  const stylesFromParent = props.style ? props.style : {};
  return (
    <div
      style={Object.assign(
        {},
        {
          height: defaultItemHeight,
          width: defaultItemWidth,
        },
        stylesFromParent,
      )}
      className={styles.item_container}
    >
      {/* list container */}
      <div className={styles.item}>
        <div className={styles.item_options}>{modelOptions()}</div>
        <div className={styles.status}>{modelStatusComponent()}</div>
        <div className={styles.title}>
          <span
            style={{ width: defaultItemWidth - 10 }}
            className={styles.elipsis}
            title={item.name}
          >
            {item.name}
          </span>
        </div>
        <div className={styles.date}>
          上传时间：{item.updateDate.split(' ')[0]}
        </div>
      </div>
    </div>
  );
}

export default ListItemComp;
